mapper.select:
select no, id, title, contents, to_char(regdate, 'yyyy-mm-dd') regdate
from board
where no = #{no:INTEGER}

select bno, price, dealstate, mno, dno, title, contents, to_char(regdate, 'yyyy-mm-dd') regdate
from buyboard
where bno = #{bno:INTEGER}

select bno, price, dealstate, mno, dno, title, contents, to_char(regdate, 'yyyy-mm-dd') regdate
from sellboard
where bno = #{bno:INTEGER}
