mapper.select:
SELECT  c.* , fno, rfilename, sfilename      
FROM  (    SELECT b.*   
           FROM ( SELECT ROWNUM ro, a.*    
                    FROM   ( select bno, price, cno, mno, title, contents, to_char(regdate, 'yyyy-mm-dd') regdate
                              from BUYBOARD
                              order by regdate DESC
                            ) a
                 ) b   
              
      ) c, BUYBOARDFILE  bf 
WHERE   c.bno = bf.bno(+)
	
		
					
				and mno =#{word}
			
					
				and title like '%'||#{word}||'%'
			
					
				and contents like '%'||#{word}||'%'

SELECT  c.* , fno, rfilename, sfilename      
FROM  (    SELECT b.*   
           FROM ( SELECT ROWNUM ro, a.*    
                    FROM   ( select bno, price, cno, mno, title, contents, to_char(regdate, 'yyyy-mm-dd') regdate
                              from SELLBOARD
                              order by regdate DESC
                            ) a
                 ) b   
                   
      ) c, SELLBOARDFILE  bf 
WHERE   c.bno = bf.bno(+)
	
		
					
				and mno =#{word}
			
					
				and title like '%'||#{word}||'%'
			
					
				and contents like '%'||#{word}||'%'

select count(*)
from buyreply
where bno = #{bno:INTEGER}

select count(*)
from sellreply
where bno = #{bno:INTEGER}

select count(*)
from cart
where mno = #{mno} and bno = #{bno} and bkind = #{sellbuy}
